    clock.tick(FPS)
    display.blit(background, (0,0))
    level5.draw()
    player.update(level5)
    if player.levelCompleted:
        Player.drawtext(f"Level Complete!",font,(0,0,0),400,450)
        nextLevelImage = pygame.image.load("images/buttons/NextLevelButton.png")
